import React from "react";
import { useParams } from "react-router-dom";
import { fetchPost } from "../services/api";

const BlogPost = () => {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    const getPost = async () => {
      const data = await fetchPost(id);
      setPost(data);
    };
    getPost();
  }, [id]);

  if (!post) return <div>Loading...</div>;

  return (
    <div className="blog-post">
      <h1>{post.title}</h1>
      <img src={post.image} alt={post.title} />
      <h3>By {post.author}</h3>
      <a href={post.authorLinkedIn} target="_blank" rel="noopener noreferrer">
        LinkedIn Profile
      </a>
      <p>{post.content}</p>
      <div className="social-share">
        <button className="facebook" onClick={() => window.open(`https://www.facebook.com/sharer/sharer.php?u=${window.location.href}`, '_blank')}>
          Share on Facebook
        </button>
        <button className="twitter" onClick={() => window.open(`https://twitter.com/intent/tweet?url=${window.location.href}`, '_blank')}>
          Share on Twitter
        </button>
        <button className="linkedin" onClick={() => window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${window.location.href}`, '_blank')}>
          Share on LinkedIn
        </button>
      </div>
    </div>
  );
};

export default BlogPost;
